    18	import org.bouncycastle.operator.jcajce.JcaContentSignerBuilder;
    19	
    20	import java.io.FileWriter;
    21	import java.math.BigInteger;
    22	import java.security.*;
    23	import java.security.cert.X509Certificate;
    24	import java.util.Date;
    25	
    26	
    27	@Slf4j
    28	public class CertificateGenerator {
    29	
    30	    public static void generate() throws Exception {
    31	        String keyPath = String.format("%s/ca.key", Constant.PATH);
    32	        String crtPath = String.format("%s/ca.crt", Constant.PATH);
    33	        String keyContent = FileUtil.readUtf8String(keyPath);
    34	        String crtContent = FileUtil.readUtf8String(crtPath);
    35	        if (StrUtil.isAllNotEmpty(keyContent, crtContent)) {
    36	            log.warn("证书和私钥已存在，无需重新生成");
    37	            return;
    38	        }
    39	
    40	        Security.addProvider(new BouncyCastleProvider());
    41	
    42	        KeyPairGenerator keyGen = KeyPairGenerator.getInstance("RSA");
    43	        keyGen.initialize(4096, new SecureRandom());
    44	        KeyPair keyPair = keyGen.generateKeyPair();
    45	        PrivateKey privateKey = keyPair.getPrivate();
    46	        PublicKey publicKey = keyPair.getPublic();
    47	
    48	        X500Name issuerName = new X500Name("CN=JetProfile CA");
    49	        X500Name subjectName = new X500Name("CN=Novice-from-2024-01-19");
    50	        BigInteger serialNumber = BigInteger.valueOf(System.currentTimeMillis());
    51	        DateTime today = DateUtil.beginOfDay(DateUtil.date());
    52	        // Yesterday
    53	        Date notBefore = DateUtil.offset(today, DateField.DAY_OF_MONTH, -1);
    54	        // 10 years later
    55	        Date notAfter = DateUtil.offset(today, DateField.YEAR, 10);
    56	
    57	        SubjectPublicKeyInfo subPubKeyInfo = SubjectPublicKeyInfo.getInstance(publicKey.getEncoded());
    58	        X509v3CertificateBuilder certBuilder = new JcaX509v3CertificateBuilder(issuerName, serialNumber, notBefore, notAfter, subjectName, subPubKeyInfo);
    59	
    60	        ContentSigner signer = new JcaContentSignerBuilder("SHA256withRSA").build(privateKey);
    61	        X509Certificate cert = new JcaX509CertificateConverter().setProvider("BC").getCertificate(certBuilder.build(signer));
    62	        // 将私钥写入 PEM 文件
    63	        try (JcaPEMWriter pemWriter = new JcaPEMWriter(new FileWriter(keyPath))) {
    64	            pemWriter.writeObject(privateKey);
    65	        }
    66	
    67	        // 将证书写入 PEM 文件
    68	        try (JcaPEMWriter pemWriter = new JcaPEMWriter(new FileWriter(crtPath))) {
    69	            pemWriter.writeObject(cert);
    70	        }
    71	    }
    72	}
