    56	    public static void generate() throws Exception {
    57	        CertificateFactory certificateFactory = CertificateFactory.getInstance("X.509");
    58	        X509Certificate cert = (X509Certificate) certificateFactory.generateCertificate(Files.newInputStream(Paths.get(String.format("%s/ca.crt", Constant.PATH))));
    59	
    60	        // x：证书的签名密文
    61	        BigInteger x = new BigInteger(1, cert.getSignature());
    62	
    63	        // y：证书指数 固定65537
    64	        BigInteger y = new BigInteger("65537");
    65	
    66	        // z：内置根证书的公钥
    67	        X509Certificate rootCertificate = (X509Certificate) certificateFactory.generateCertificate(new ByteArrayInputStream(ROOT_CERTIFICATE.getBytes(StandardCharsets.UTF_8)));
    68	        RSAPublicKey publicKey = (RSAPublicKey) rootCertificate.getPublicKey();
    69	        BigInteger z = publicKey.getModulus();
