    42	        codes = ArrayUtil.isEmpty(codes) ? Constant.DEFAULT_CODES : codes;
    43	        String licenseId = RandomUtil.randomString(RandomUtil.BASE_CHAR_NUMBER_LOWER.toUpperCase(), 10);
