    68	
    69	    static PrivateKey getPrivateKey() throws Exception {
    70	        Security.addProvider(new BouncyCastleProvider());
    71	        PEMParser pemParser = new PEMParser(new FileReader(FileUtil.getAbsolutePath(String.format("%s/ca.key", Constant.PATH))));
    72	        JcaPEMKeyConverter converter = new JcaPEMKeyConverter().setProvider("BC");
    73	        Object object = pemParser.readObject();
    74	        KeyPair kp = converter.getKeyPair((PEMKeyPair) object);
    75	        return kp.getPrivate();
    76	    }
