     9	import cn.hutool.core.util.RandomUtil;
    10	import cn.hutool.json.JSONUtil;
    11	import com.cactusli.license.constant.Constant;
    12	import com.cactusli.license.domain.LicensePart;
    13	import lombok.extern.slf4j.Slf4j;
    14	import org.bouncycastle.jce.provider.BouncyCastleProvider;
    15	import org.bouncycastle.openssl.PEMKeyPair;
    16	import org.bouncycastle.openssl.PEMParser;
    17	import org.bouncycastle.openssl.jcajce.JcaPEMKeyConverter;
    18	
    19	import java.io.FileReader;
    20	import java.nio.charset.StandardCharsets;
    21	import java.nio.file.Files;
    22	import java.nio.file.Paths;
    23	import java.security.KeyPair;
