   228	    <script>
   229	        let currentLicense = '';
   230	        
   231	        // 生成许可证
   232	        function generateLicense() {
   233	            const button = event.target;
   234	            const originalText = button.innerHTML;
   235	            button.innerHTML = '<i class="fas fa-spinner fa-spin me-1"></i>生成中...';
   236	            button.disabled = true;
   237	            
   238	            $.ajax({
   239	                url: '/generate',
   240	                type: 'POST',
   241	                success: function(response) {
   242	                    if (response.success) {
   243	                        currentLicense = response.license;
   244	                        $('#licenseOutput').text(response.license);
   245	                        $('#licenseResult').show();
   246	                        showToast(response.message, 'success');
   247	                    } else {
   248	                        showToast(response.message, 'error');
   249	                    }
   250	                },
   251	                error: function() {
   252	                    showToast('请求失败，请稍后重试', 'error');
   253	                },
   254	                complete: function() {
   255	                    button.innerHTML = originalText;
   256	                    button.disabled = false;
   257	                }
   258	            });
   259	        }
   260	        
   261	        // 生成指定产品的许可证
   262	        function generateProductLicense(productType) {
   263	            const button = event.target;
   264	            const originalText = button.innerHTML;
   265	            button.innerHTML = '<i class="fas fa-spinner fa-spin me-1"></i>生成中...';
   266	            button.disabled = true;
   267	            
   268	            $.ajax({
   269	                url: '/generate/' + productType,
   270	                type: 'POST',
   271	                success: function(response) {
   272	                    if (response.success) {
   273	                        currentLicense = response.license;
   274	                        $('#licenseOutput').text(response.license);
   275	                        $('#licenseResult').show();
   276	                        showToast(response.message + ' (' + response.productType + ')', 'success');
   277	                    } else {
   278	                        showToast(response.message, 'error');
   279	                    }
   280	                },
   281	                error: function() {
   282	                    showToast('请求失败，请稍后重试', 'error');
   283	                },
   284	                complete: function() {
   285	                    button.innerHTML = originalText;
   286	                    button.disabled = false;
   287	                }
   288	            });
