    26	     * 许可证有效期（年）
    27	     */
    28	    private int licenseYears = Constant.DEFAULT_LICENSE_YEARS;
    29	    
    30	    /**
    31	     * 证书颁发者
    32	     */
    33	    private String issuerName = Constant.DEFAULT_ISSUER_NAME;
    34	    
    35	    /**
    36	     * 证书主题
    37	     */
    38	    private String subjectName = Constant.DEFAULT_SUBJECT_NAME;
    39	    
    40	    /**
    41	     * 产品代码
    42	     */
    43	    private String[] productCodes = Constant.DEFAULT_CODES;
    44	    
    45	    /**
    46	     * 预设产品类型
    47	     */
    48	    private String productType = "IDEA";
    49	    
    50	    /**
    51	     * 构造函数
    52	     */
    53	    public LicenseConfig() {
    54	    }
    55	    
    56	    /**
    57	     * 带参数的构造函数
    58	     */
    59	    public LicenseConfig(String certPath, String licenseeName, int licenseYears, 
    60	                        String issuerName, String subjectName, String[] productCodes) {
    61	        this.certPath = StrUtil.isNotBlank(certPath) ? certPath : Constant.DEFAULT_PATH;
    62	        this.licenseeName = StrUtil.isNotBlank(licenseeName) ? licenseeName : Constant.DEFAULT_LICENSEE_NAME;
    63	        this.licenseYears = licenseYears > 0 ? licenseYears : Constant.DEFAULT_LICENSE_YEARS;
    64	        this.issuerName = StrUtil.isNotBlank(issuerName) ? issuerName : Constant.DEFAULT_ISSUER_NAME;
    65	        this.subjectName = StrUtil.isNotBlank(subjectName) ? subjectName : Constant.DEFAULT_SUBJECT_NAME;
    66	        this.productCodes = ArrayUtil.isNotEmpty(productCodes) ? productCodes : Constant.DEFAULT_CODES;
    67	    }
    68	    
    69	    /**
    70	     * 根据产品类型设置产品代码
    71	     */
    72	    public void setProductType(String productType) {
    73	        this.productType = productType;
    74	        switch (productType.toUpperCase()) {
    75	            case "IDEA":
    76	                this.productCodes = Constant.IDEA_CODES;
    77	                break;
    78	            case "WEBSTORM":
    79	                this.productCodes = Constant.WEBSTORM_CODES;
    80	                break;
    81	            case "PYCHARM":
    82	                this.productCodes = Constant.PYCHARM_CODES;
    83	                break;
    84	            case "GOLAND":
    85	                this.productCodes = Constant.GOLAND_CODES;
    86	                break;
    87	            case "CLION":
    88	                this.productCodes = Constant.CLION_CODES;
    89	                break;
    90	            case "PHPSTORM":
    91	                this.productCodes = Constant.PHPSTORM_CODES;
    92	                break;
    93	            case "RIDER":
    94	                this.productCodes = Constant.RIDER_CODES;
    95	                break;
    96	            case "DATAGRIP":
    97	                this.productCodes = Constant.DATAGRIP_CODES;
    98	                break;
    99	            case "RUBYMINE":
   100	                this.productCodes = Constant.RUBYMINE_CODES;
   101	                break;
   102	            default:
   103	                this.productCodes = Constant.DEFAULT_CODES;
   104	                break;
   105	        }
   106	    }
   107	    
   108	    /**
   109	     * 应用配置到常量类
   110	     */
   111	    public void applyToConstants() {
   112	        Constant.updateConfig(this.certPath, this.licenseeName, this.licenseYears, 
   113	                             this.issuerName, this.subjectName);
   114	    }
   115	    
   116	    /**
   117	     * 验证配置
   118	     */
   119	    public boolean isValid() {
   120	        return StrUtil.isNotBlank(certPath) && 
   121	               StrUtil.isNotBlank(licenseeName) && 
   122	               licenseYears > 0 && 
   123	               StrUtil.isNotBlank(issuerName) && 
   124	               StrUtil.isNotBlank(subjectName) && 
   125	               ArrayUtil.isNotEmpty(productCodes);
   126	    }
   127	    
   128	    /**
   129	     * 获取配置摘要
   130	     */
   131	    public String getSummary() {
   132	        return String.format("证书路径: %s, 持有者: %s, 有效期: %d年, 产品类型: %s, 产品代码: %s", 
   133	                           certPath, licenseeName, licenseYears, productType, 
   134	                           ArrayUtil.join(productCodes, ","));
   135	    }
   136	}
