Path: src/main/java/com/cactusli/license/MyAgent.java
     1	package com.cactusli.license;
     2	
     3	import com.cactusli.license.bytecodes.BigIntegerAdvice;
     4	import com.cactusli.license.bytecodes.HttpClientAdvice;
     5	import net.bytebuddy.agent.builder.AgentBuilder;
     6	import net.bytebuddy.asm.Advice;
     7	import net.bytebuddy.matcher.ElementMatchers;
     8	
     9	import java.lang.instrument.Instrumentation;
    10	
    11	/**
    12	 * Package: com.lemonzuo.license
    13	 * Description:
    14	 *
    15	 * @Author 仙人球⁶ᴳ | 微信：Cactusesli
    16	 * @Date 2024/2/22 15:12
    17	 * @Github https://github.com/lixuanfengs
    18	 */
    19	public class MyAgent {
    20	    public static void premain(String agentArgs, Instrumentation inst) throws Exception {
    21	        System.out.println("premain");
    22	        AgentBuilder agentBuilder = new AgentBuilder.Default()
    23	                .with(AgentBuilder.RedefinitionStrategy.RETRANSFORMATION)
    24	                .with(AgentBuilder.InitializationStrategy.NoOp.INSTANCE)
    25	                .with(AgentBuilder.TypeStrategy.Default.REDEFINE)
    26	                .ignore(ElementMatchers.nameStartsWith("net.bytebuddy."));
    27	//                .with(new MyAgentListener());
    28	        // 使得证书验证通过
    29	        agentBuilder.type(ElementMatchers.named("java.math.BigInteger"))
    30	                .transform((builder, typeDescription, classLoader, module, protectionDomain) -> builder
    31	                        .visit(Advice.to(BigIntegerAdvice.class)
    32	                                .on(ElementMatchers.named("oddModPow"))))
    33	                .installOn(inst);
    34	        // 使得软件无法联查询license是否有效
    35	        agentBuilder.type(ElementMatchers.named("sun.net.www.http.HttpClient"))
    36	                .transform((builder, typeDescription, classLoader, module, protectionDomain) -> builder
    37	                        .visit(Advice.to(HttpClientAdvice.class)
    38	                                .on(ElementMatchers.named("openServer"))))
    39	                .installOn(inst);
    40	
    41	    }
    42	}
