    31	@Slf4j
    32	public class LicenseGenerator {
    33	
    34	    public static void generate(String... codes) throws Exception {
    35	        CertificateFactory certificateFactory = CertificateFactory.getInstance("X.509");
    36	        X509Certificate cert = (X509Certificate) certificateFactory.generateCertificate(Files.newInputStream(Paths.get(String.format("%s/ca.crt", Constant.PATH))));
    37	
    38	        // 自己修改 license内容
    39	        DateTime endOfToday = DateUtil.endOfDay(DateUtil.date());
    40	        // 偏移10年
    41	        DateTime effectiveDate = DateUtil.offset(endOfToday, DateField.YEAR, 10);
    42	        codes = ArrayUtil.isEmpty(codes) ? Constant.DEFAULT_CODES : codes;
    43	        String licenseId = RandomUtil.randomString(RandomUtil.BASE_CHAR_NUMBER_LOWER.toUpperCase(), 10);
    44	
    45	        LicensePart license = new LicensePart(licenseId, Constant.LICENSEE_NAME, codes, DateUtil.formatDate(effectiveDate));
    46	
    47	        String licensePart = JSONUtil.toJsonStr(license);
    48	        log.info("licensePart: {}", licensePart);
    49	
    50	        byte[] licensePartBytes = licensePart.getBytes(StandardCharsets.UTF_8);
    51	        String licensePartBase64 = Base64.encode(licensePartBytes);
    52	
    53	        PrivateKey privateKey = getPrivateKey();
    54	        Signature signature = Signature.getInstance("SHA1withRSA");
    55	        signature.initSign(privateKey);
    56	
    57	        signature.update(licensePartBytes);
    58	        byte[] signatureBytes = signature.sign();
    59	
    60	        String sigResultsBase64 = Base64.encode(signatureBytes);
    61	        // Combine results as needed
    62	        String result = licenseId + "-" + licensePartBase64 + "-" + sigResultsBase64 + "-" + Base64.encode(cert.getEncoded());
    63	
    64	        log.info("================== Activation code ==================");
    65	        log.info("Activation code: {}", result);
    66	        log.info("================== Activation code ==================");
    67	    }
    68	
    69	    static PrivateKey getPrivateKey() throws Exception {
    70	        Security.addProvider(new BouncyCastleProvider());
    71	        PEMParser pemParser = new PEMParser(new FileReader(FileUtil.getAbsolutePath(String.format("%s/ca.key", Constant.PATH))));
    72	        JcaPEMKeyConverter converter = new JcaPEMKeyConverter().setProvider("BC");
    73	        Object object = pemParser.readObject();
    74	        KeyPair kp = converter.getKeyPair((PEMKeyPair) object);
    75	        return kp.getPrivate();
    76	    }
