     1	package com.cactusli.license.service;
     2	
     3	import com.cactusli.license.config.LicenseConfig;
     4	import com.cactusli.license.generator.CertificateGenerator;
     5	import com.cactusli.license.generator.LicenseGenerator;
     6	import com.cactusli.license.generator.PowerConfRuleGenerator;
     7	import lombok.RequiredArgsConstructor;
     8	import lombok.extern.slf4j.Slf4j;
     9	import org.springframework.stereotype.Service;
    10	
    11	/**
    12	 * 许可证服务类
    13	 * 
    14	 * @author CactusLi
    15	 * @version 2.0.0
    16	 */
    17	@Slf4j
    18	@Service
    19	@RequiredArgsConstructor
    20	public class LicenseService {
    21	    
    22	    private final LicenseConfig licenseConfig;
    23	    
    24	    /**
    25	     * 生成完整的许可证
    26	     * 
    27	     * @return 生成的许可证字符串
    28	     */
    29	    public String generateLicense() {
    30	        try {
    31	            log.info("开始生成许可证，配置信息: {}", licenseConfig.getSummary());
    32	            
    33	            // 应用配置到常量类
    34	            licenseConfig.applyToConstants();
    35	            
    36	            // 1. 生成证书和私钥
    37	            log.info("步骤1: 生成证书和私钥");
    38	            CertificateGenerator.generate();
    39	            
    40	            // 2. 生成证书校验规则
    41	            log.info("步骤2: 生成证书校验规则");
    42	            PowerConfRuleGenerator.generate();
    43	            
    44	            // 3. 生成许可证
    45	            log.info("步骤3: 生成许可证");
    46	            String license = LicenseGenerator.generate(licenseConfig.getProductCodes());
    47	            
    48	            log.info("许可证生成成功");
    49	            return license;
    50	            
    51	        } catch (Exception e) {
    52	            log.error("生成许可证失败", e);
    53	            throw new RuntimeException("生成许可证失败: " + e.getMessage(), e);
    54	        }
    55	    }
    56	    
    57	    /**
    58	     * 生成指定产品的许可证
    59	     * 
    60	     * @param productCodes 产品代码数组
    61	     * @return 生成的许可证字符串
    62	     */
    63	    public String generateLicense(String[] productCodes) {
    64	        try {
    65	            log.info("开始生成指定产品的许可证，产品代码: {}", String.join(",", productCodes));
    66	            
    67	            // 应用配置到常量类
    68	            licenseConfig.applyToConstants();
    69	            
    70	            // 1. 生成证书和私钥
    71	            CertificateGenerator.generate();
    72	            
    73	            // 2. 生成证书校验规则
    74	            PowerConfRuleGenerator.generate();
    75	            
    76	            // 3. 生成许可证
    77	            String license = LicenseGenerator.generate(productCodes);
    78	            
    79	            log.info("指定产品许可证生成成功");
    80	            return license;
    81	            
    82	        } catch (Exception e) {
    83	            log.error("生成指定产品许可证失败", e);
    84	            throw new RuntimeException("生成许可证失败: " + e.getMessage(), e);
    85	        }
    86	    }
