    51	     * 证书主题
    52	     */
    53	    @NotBlank(message = "证书主题不能为空")
    54	    private String subjectName = Constant.DEFAULT_SUBJECT_NAME;
    55	    
    56	    /**
    57	     * 产品代码
    58	     */
    59	    @NotNull(message = "产品代码不能为空")
    60	    private String[] productCodes = Constant.DEFAULT_CODES;
    61	    
    62	    /**
    63	     * 预设产品类型
    64	     */
    65	    @NotBlank(message = "产品类型不能为空")
    66	    private String productType = "IDEA";
    67	    
    68	    /**
    69	     * 构造函数
    70	     */
    71	    public LicenseConfig() {
    72	    }
    73	    
    74	    /**
    75	     * 带参数的构造函数
    76	     */
    77	    public LicenseConfig(String certPath, String licenseeName, int licenseYears, 
    78	                        String issuerName, String subjectName, String[] productCodes) {
    79	        this.certPath = StrUtil.isNotBlank(certPath) ? certPath : Constant.DEFAULT_PATH;
    80	        this.licenseeName = StrUtil.isNotBlank(licenseeName) ? licenseeName : Constant.DEFAULT_LICENSEE_NAME;
    81	        this.licenseYears = licenseYears > 0 ? licenseYears : Constant.DEFAULT_LICENSE_YEARS;
    82	        this.issuerName = StrUtil.isNotBlank(issuerName) ? issuerName : Constant.DEFAULT_ISSUER_NAME;
    83	        this.subjectName = StrUtil.isNotBlank(subjectName) ? subjectName : Constant.DEFAULT_SUBJECT_NAME;
    84	        this.productCodes = ArrayUtil.isNotEmpty(productCodes) ? productCodes : Constant.DEFAULT_CODES;
    85	    }
    86	    
    87	    /**
    88	     * 根据产品类型设置产品代码
    89	     */
    90	    public void setProductType(String productType) {
    91	        this.productType = productType;
    92	        switch (productType.toUpperCase()) {
    93	            case "IDEA":
    94	                this.productCodes = Constant.IDEA_CODES;
    95	                break;
    96	            case "WEBSTORM":
    97	                this.productCodes = Constant.WEBSTORM_CODES;
    98	                break;
    99	            case "PYCHARM":
   100	                this.productCodes = Constant.PYCHARM_CODES;
   101	                break;
   102	            case "GOLAND":
   103	                this.productCodes = Constant.GOLAND_CODES;
   104	                break;
   105	            case "CLION":
   106	                this.productCodes = Constant.CLION_CODES;
   107	                break;
   108	            case "PHPSTORM":
   109	                this.productCodes = Constant.PHPSTORM_CODES;
   110	                break;
   111	            case "RIDER":
   112	                this.productCodes = Constant.RIDER_CODES;
   113	                break;
   114	            case "DATAGRIP":
   115	                this.productCodes = Constant.DATAGRIP_CODES;
   116	                break;
   117	            case "RUBYMINE":
   118	                this.productCodes = Constant.RUBYMINE_CODES;
   119	                break;
   120	            default:
   121	                this.productCodes = Constant.DEFAULT_CODES;
   122	                break;
   123	        }
   124	    }
   125	    
   126	    /**
   127	     * 应用配置到常量类
   128	     */
   129	    public void applyToConstants() {
   130	        Constant.updateConfig(this.certPath, this.licenseeName, this.licenseYears, 
   131	                             this.issuerName, this.subjectName);
   132	    }
   133	    
   134	    /**
   135	     * 验证配置
   136	     */
   137	    public boolean isValid() {
   138	        return StrUtil.isNotBlank(certPath) && 
   139	               StrUtil.isNotBlank(licenseeName) && 
   140	               licenseYears > 0 && 
   141	               StrUtil.isNotBlank(issuerName) && 
   142	               StrUtil.isNotBlank(subjectName) && 
   143	               ArrayUtil.isNotEmpty(productCodes);
   144	    }
   145	    
   146	    /**
   147	     * 获取配置摘要
   148	     */
   149	    public String getSummary() {
   150	        return String.format("证书路径: %s, 持有者: %s, 有效期: %d年, 产品类型: %s, 产品代码: %s", 
   151	                           certPath, licenseeName, licenseYears, productType, 
   152	                           ArrayUtil.join(productCodes, ","));
   153	    }
   154	    
   155	    /**
   156	     * 重置为默认配置
   157	     */
   158	    public void resetToDefaults() {
   159	        this.certPath = Constant.DEFAULT_PATH;
   160	        this.licenseeName = Constant.DEFAULT_LICENSEE_NAME;
   161	        this.licenseYears = Constant.DEFAULT_LICENSE_YEARS;
   162	        this.issuerName = Constant.DEFAULT_ISSUER_NAME;
   163	        this.subjectName = Constant.DEFAULT_SUBJECT_NAME;
   164	        this.productCodes = Constant.DEFAULT_CODES;
   165	        this.productType = "IDEA";
   166	    }
