package com.cactusli.license.bytecodes;

import net.bytebuddy.asm.Advice;
import net.bytebuddy.implementation.bytecode.assign.Assigner;

import java.math.BigInteger;

/**
 * Package: com.lemonzuo.license.HttpAd
 * Description:
 *
 * @Author 仙人球⁶ᴳ | 微信：Cactusesli
 * @Date 2024/2/22 15:15
 * @Github https://github.com/lixuanfengs
 */
public class BigIntegerAdvice {

    @Advice.OnMethodExit
    public static void intercept(
            @Advice.This Object x,
            @Advice.Argument(0) Object y,
            @Advice.Argument(1) Object z,
            @Advice.Return(readOnly = false, typing = Assigner.Typing.DYNAMIC) Object result) {
        if (x.equals(new BigInteger(""))){
            if (y.equals(new BigInteger("65537"))){
                if (z.equals(new BigInteger(""))){
                    result = new BigInteger("");
                }
            }
        }
    }

}
